        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389255, 'Cimetidine', 'Magaldrate', 'Minor', 'Antacids and some aluminum, calcium, and magnesium salts may decrease the plasma concentrations of H2-receptor antagonists during oral coadministration. The mechanism of interaction is unknown, but may involve reduced oral absorption due to increased gastric pH. The clinical significance has not been established. As a precaution, patients may consider taking H2-receptor antagonists one to two hours before antacids.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
