    ) VALUES (1389260, 'Cimetidine', 'Mebendazole', 'Minor', 'Cimetidine may decrease the hepatic metabolism of mebendazole. Serum levels of mebendazole may be increased. Since plasma levels of mebendazole vary greatly among patients receiving this drug, the clinical significance of this interaction is not well established.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
