    ) VALUES (1389269, 'Cimetidine', 'Methohexital', 'Minor', 'Barbiturates may slightly reduce the bioavailability of cimetidine. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
