        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389314, 'Cimetidine', 'Penbutolol', 'Moderate', 'H2 antagonists such as cimetidine increase plasma concentrations of some oral beta-blockers. The mechanism may be related to inhibition of CYP450 metabolism of hepatically metabolized beta-blockers, and/or inhibition of renal tubular secretion of others.', NULL, 'Clinical monitoring of patient response and tolerance is recommended when cimetidine is added to or discontinued from a beta-blocker regimen.', NULL, 'Clinical monitoring of patient response and tolerance is recommended when cimetidine is added to or discontinued from a beta-blocker regimen.', 'Metabolism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
