        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389315, 'Cimetidine', 'Pentazocine', 'Moderate', 'Cimetidine may increase the action of narcotic analgesics. Cases of respiratory depression and apnea have been reported. The mechanism is unknown.', NULL, 'Patients'' vital signs should be closely monitored while receiving this combination.', NULL, 'Patients'' vital signs should be closely monitored while receiving this combination. If clinically significant respiratory depression occurs, the drugs should be withdrawn. The use of a narcotic antagonist, such as naloxone, may be needed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
