        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389332, 'Cimetidine', 'Pralsetinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pralsetinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when pralsetinib is used with CYP450 3A inhibitors.', NULL, 'Caution is advised when pralsetinib is used with CYP450 3A inhibitors. Monitor closely for adverse effects whenever a CYP450 3A4 inhibitor is added to therapy. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
