        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389333, 'Cimetidine', 'Pramipexole', 'Moderate', 'Cimetidine has been shown to cause a 50% increase in the pramipexole area under the concentration-time curve and a 40% increase in its half-life by inhibiting renal tubular secretion of pramipexole. Monitoring patients for altered efficacy and safety is recommended.', NULL, 'Patients should be advised to notify their physician if they experience excessive drowsiness, somnolence, or orthostasis.', NULL, 'Patients should be advised to notify their physician if they experience excessive drowsiness, somnolence, or orthostasis.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
