        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389362, 'Cimetidine', 'Ropinirole', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 1A2 may increase the plasma concentrations of ropinirole, which is metabolized by the isoenzyme.', NULL, 'Pharmacologic response to ropinirole should be monitored more closely whenever a CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the ropinirole dosage adjusted as necessary.', NULL, 'Pharmacologic response to ropinirole should be monitored more closely whenever a CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the ropinirole dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
