INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389394, 'Cimetidine', 'Thalidomide', 'Moderate', 'Thalidomide can cause bradycardia and may have additive effects with other drugs that decrease heart rate. Cases of bradycardia have been associated with thalidomide use, some requiring medical interventions. The clinical significance and underlying etiology of the bradycardia observed with thalidomide treatment have not been established. However, the potential for additive bradycardic effects should be considered when used with other drugs that slow cardiac conduction such as beta-blockers, calcium channel blockers, digitalis, flecainide, moricizine, and propafenone. Non-cardiac drugs that may also cause bradycardia include atazanavir, lacosamide, lithium, mefloquine, succinylcholine, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Because bradycardia is a risk factor for torsade de pointes arrhythmia, concerns also exist when thalidomide is used with drugs that prolong the QT interval or cause torsade de pointes.', NULL, 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia.', NULL, 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat. A dose reduction of thalidomide or discontinuation may be required.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389395, 'Cimetidine', 'Theophylline', 'Moderate', 'Cimetidine may increase theophylline plasma concentrations by as much as 70%. The mechanism is related to inhibition of liver CYP450 enzymes responsible for theophylline metabolism. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', NULL, 'Clinical monitoring of patient response, tolerance, and laboratory theophylline serum concentrations is recommended.', NULL, 'Clinical monitoring of patient response, tolerance, and laboratory theophylline serum concentrations is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389396, 'Cimetidine', 'Thiopental', 'Minor', 'Barbiturates may slightly reduce the bioavailability of cimetidine. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389397, 'Cimetidine', 'Thioridazine', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of thioridazine and its two active metabolites, mesoridazine and sulforidazine, all of which are substrates of the isoenzyme.', NULL, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.', NULL, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated. Depending on the elimination half-life of these drugs, a considerable waiting period may be appropriate following their discontinuation before thioridazine is initiated. For example, the manufacturer of fluoxetine recommends that thioridazine not be administered within 5 weeks after discontinuing fluoxetine because of the drug''s long half-life.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389398, 'Cimetidine', 'Ticlopidine', 'Moderate', 'Cimetidine may reduce the clearance of ticlopidine by up to 50%. The mechanism has not been established. The clinical relevance of this interaction is unknown.', NULL, 'Consider monitoring the patient more closely for blood dyscrasias and abnormal bleeding.', NULL, 'Consider monitoring the patient more closely for blood dyscrasias and abnormal bleeding.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389399, 'Cimetidine', 'Timolol', 'Moderate', 'H2 antagonists such as cimetidine increase plasma concentrations of some oral beta-blockers. The mechanism may be related to inhibition of CYP450 metabolism of hepatically metabolized beta-blockers, and/or inhibition of renal tubular secretion of others.', NULL, 'Clinical monitoring of patient response and tolerance is recommended when cimetidine is added to or discontinued from a beta-blocker regimen.', NULL, 'Clinical monitoring of patient response and tolerance is recommended when cimetidine is added to or discontinued from a beta-blocker regimen.', 'Metabolism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389400, 'Cimetidine', 'Tinidazole', 'Minor', 'Coadministration with cimetidine may increase the serum concentrations of nitroimidazoles. The interaction has been reported with metronidazole. The proposed mechanism is cimetidine inhibition of the hepatic metabolism of metronidazole. No data are available for tinidazole. This interaction is unlikely to be of clinical significance.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389401, 'Cimetidine', 'Tiotropium', 'Minor', 'Coadministration with cimetidine is not expected to significantly alter the pharmacokinetics of orally inhaled tiotropium. In one study, concomitant administration of cimetidine (400 mg three times daily) and tiotropium (14.4 mcg intravenously over 15 minutes) resulted in a 20% increase in the 4-hour area under the plasma concentration-time curve (AUC) and a 28% decrease in the renal clearance of tiotropium. Tiotropium peak plasma concentration (Cmax) and total amount excreted in urine over 96 hours were not affected. These changes are unlikely to be of clinical significance.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389402, 'Cimetidine', 'Tizanidine', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may significantly increase the plasma concentrations and pharmacologic effects of tizanidine, which is a sensitive substrate of the isoenzyme.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided.', NULL, 'Concomitant use of tizanidine with CYP450 1A2 inhibitors should generally be avoided. Otherwise, caution is advised if coadministration is required. Dosage adjustments may be necessary in patients who experience excessive adverse effects of tizanidine such as drowsiness, dizziness, lightheadedness, hypotension, and bradycardia.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389403, 'Cimetidine', 'Tocainide', 'Minor', 'Cimetidine decreases the bioavailability of tocainide in healthy subjects. The mechanism and clinical significance are unknown. Management consists of monitoring the patient''s response to tocainide and consideration of checking plasma tocainide concentrations after 3 days of coadministration. Famotidine, nizatidine, and ranitidine are not expected to interact and may be considered as alternatives.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389404, 'Cimetidine', 'Tolazamide', 'Moderate', 'H2 antagonists such as cimetidine and ranitidine may increase plasma concentrations of sulfonylureas and enhance hypoglycemic effects. The mechanism may be related to inhibition of liver cytochrome P450 enzymes responsible for sulfonylurea metabolism or increased absorption due to altered gastric pH. Cimetidine may also inhibit glucose metabolism. Other H2 antagonists may interact in a similar manner, particularly if increased pH is involved.', NULL, 'Clinical monitoring of patient response, tolerance, and glycemic control is recommended.', NULL, 'Clinical monitoring of patient response, tolerance, and glycemic control is recommended. Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs. The sulfonylurea dosage may require reduction in affected patients.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389405, 'Cimetidine', 'Tolbutamide', 'Moderate', 'H2 antagonists such as cimetidine and ranitidine may increase plasma concentrations of sulfonylureas and enhance hypoglycemic effects. The mechanism may be related to inhibition of liver cytochrome P450 enzymes responsible for sulfonylurea metabolism or increased absorption due to altered gastric pH. Cimetidine may also inhibit glucose metabolism. Other H2 antagonists may interact in a similar manner, particularly if increased pH is involved.', NULL, 'Clinical monitoring of patient response, tolerance, and glycemic control is recommended.', NULL, 'Clinical monitoring of patient response, tolerance, and glycemic control is recommended. Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs. The sulfonylurea dosage may require reduction in affected patients.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389406, 'Cimetidine', 'Tolmetin', 'Minor', 'H2 antagonists may alter the disposition of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased or decreased plasma concentrations. Data are varied, even for the same NSAID. The mechanism may be related to inhibition of metabolism, changes in gastric pH that decrease absorption, and/or reduced urinary elimination. Statistically significant changes have been small and of limited clinical significance. Clinical monitoring of patient response and tolerance is recommended.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389407, 'Cimetidine', 'Tolterodine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered. Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389408, 'Cimetidine', 'Toremifene', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of toremifene, which is primarily metabolized by the isoenzyme. Because toremifene is associated with dose- and concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety of toremifene may be appropriate whenever a CYP450 3A inhibitor is added to or withdrawn from therapy.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety of toremifene may be appropriate whenever a CYP450 3A inhibitor is added to or withdrawn from therapy. Complete blood counts, electrolyte levels (calcium, magnesium, potassium), and liver function tests should be obtained periodically. Patients should be advised to contact their physician if they experience vaginal bleeding or potential signs of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity. Patients should seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389409, 'Cimetidine', 'Trabectedin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when trabectedin is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when trabectedin is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for toxicities such as myelosuppression, rhabdomyolysis, hepatotoxicity, and cardiomyopathy, and the trabectedin dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389410, 'Cimetidine', 'Triamterene', 'Minor', 'Cimetidine may inhibit the hepatic metabolism and renal tubular secretion of triamterene, resulting in possible increased side effects of triamterene. Clinically, this is a minor interaction. Management consists of monitoring for side effects of triamterene during coadministration.', NULL, '-', NULL, '-', 'Metabolism, Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389411, 'Cimetidine', 'Triazolam', 'Moderate', 'Administration of cimetidine with benzodiazepines may increase systemic exposure to benzodiazepines. The mechanism may be related to inhibition of CYP450 3A4 hepatic metabolism by cimetidine, a moderate inhibitor of this isoenzyme. This interaction has been reported for alprazolam, diazepam, triazolam, midazolam and chlordiazepoxide, but may also occur with other benzodiazepines.', NULL, 'The US manufacturers of alprazolam, estazolam and triazolam recommend consideration of a dose reduction when these medicines are co-administered with cimetidine.', NULL, 'The US manufacturers of alprazolam, estazolam and triazolam recommend consideration of a dose reduction when these medicines are co-administered with cimetidine. Use of H2 antagonists and benzodiazepines that are not reported to interact may also be considered. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389412, 'Cimetidine', 'Trimetrexate', 'Minor', 'Trimetrexate is metabolized by cytochrome P-450 hepatic enzymes. Theoretically, drugs that inhibit this enzyme system, such as cimetidine, may inhibit the metabolism of trimetrexate. This interaction has not yet been reported in a clinical situation.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389413, 'Cimetidine', 'Trimipramine', 'Moderate', 'Cimetidine may increase the serum concentration of tricyclic antidepressants (TCAs) to toxic levels in some patients. The mechanism may be related to inhibition of CYP450 isoenzymes, including CYP450 2D6 and CYP450 3A4, that are responsible for the hepatic metabolism of TCAs.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen.', NULL, 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen. Patients should be advised to notify their physician if they experience excessive TCA effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Lower doses of tricyclic antidepressants may be necessary. Ranitidine, famotidine, and nizatidine are not expected to interact and may be an alternative to cimetidine in this circumstance.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389414, 'Cimetidine', 'Troleandomycin', 'Moderate', 'Concomitant use of cimetidine and erythromycin may result in higher-than-normal plasma levels of erythromycin through inhibition of CYP450 metabolism by cimetidine.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain.', NULL, 'The patient should be monitored for evidence of adverse effects such as nausea, vomiting, diarrhea, or abdominal pain. Ranitidine, nizatidine and famotidine do not inhibit CYP450 and would not be expected to affect erythromycin levels at recommended doses.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389415, 'Cimetidine', 'Trospium', 'Moderate', 'Theoretically, coadministration of trospium chloride with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of trospium and/or the coadministered drug(s). The mechanism is competitive inhibition of renal excretion. Drugs that are thought to undergo active tubular secretion include acyclovir/valacyclovir, cidofovir, cimetidine, digoxin, flecainide, ganciclovir/valganciclovir, metformin, midodrine, morphine, pancuronium, procainamide, quinidine, ranitidine, tenofovir, triamterene, and vancomycin.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389416, 'Cimetidine', 'Ubrogepant', 'Moderate', 'Coadministration with moderate or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors. If needed, a second ubrogepant dose should be avoided within 24 hours of the initial dose when used with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389417, 'Cimetidine', 'Upadacitinib', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of upadacitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inhibitors.', NULL, 'Caution is recommended if upadacitinib is used with moderate CYP450 3A4 inhibitors. Pharmacologic effects of upadacitinib should be monitored more closely whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389418, 'Cimetidine', 'Valaciclovir', 'Minor', 'Cimetidine may increase the serum concentration of acyclovir. The mechanism is competitive inhibition of the renal tubular secretion of acyclovir. Initial dosage adjustments usually are not necessary. Patients should, however, be monitored for acyclovir-related toxicities. Also, cimetidine reduces the rate at which valacyclovir is converted to acyclovir. The extent of conversion is unaffected, and this interaction is not considered to be clinically significant.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389419, 'Cimetidine', 'Valdecoxib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of valdecoxib, which is metabolized by these isoenzymes.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast. Dose reductions of the COX-2 inhibitor may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389420, 'Cimetidine', 'Vardenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations of vardenafil, which is primarily metabolized by the former and secondarily by the latter. The possibility of prolonged and/or increased pharmacologic effects of vardenafil should be considered.', NULL, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.', NULL, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance. The manufacturer recommends that an initial vardenafil dosage of 2.5 to 5 mg be used in patients treated concomitantly with a potent CYP450 3A4 inhibitor such as erythromycin, itraconazole, ketoconazole, indinavir, and ritonavir.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389421, 'Cimetidine', 'Varenicline', 'Minor', 'Coadministration with cimetidine has been shown to slightly increase the plasma concentrations of varenicline. The proposed mechanism is cimetidine inhibition of the active renal secretion of varenicline via human organic cation transporter, OCT2.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
