INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109589, 'Erdafitinib', 'Sevelamer', 'Major', 'Coadministration with agents that can alter serum phosphate levels may affect the initial dosage determination of erdafitinib. The mechanism appears to be related to the pharmacodynamic effects of fibroblast growth factor receptor (FGFR) inhibition by erdafitinib. Inhibition of the FGFR receptor has been shown to lead to an increase in serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels.', NULL, 'The manufacturer recommends that concomitant use of erdafitinib with agents that may alter serum phosphate levels be avoided before its initial dose increase period, which is between days 14 to 21 of starting erdafitinib therapy and is based on serum phosphate levels. After the initial dose increase period, an oral phosphate binder may be appropriate in the management of patients with serum phosphate levels greater than 7 mg/dL. If concomitant use with agents known to increase serum phosphate levels is required after this initial dose increase period, close monitoring of patient tolerability and serum phosphate levels is recommended.', 'Others', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Difelikefalin, Promethazine, Deferoxamine, Levoleucovorin, Arginine, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109714/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109590, 'Ergocalciferol', 'Sevelamer', 'Moderate', 'INTERVAL: Bile acid sequestrants and the phosphate binder, sevelamer, can decrease the absorption of fat-soluble vitamins A, D, E, and K. In non-clinical safety studies, rats administered colesevelam at doses greater than 30-fold the projected human clinical dose developed hemorrhage in association with vitamin K deficiency. In a crossover study involving healthy subjects, coadministration of sevelamer with calcitriol resulted in a significant reduction in bioavailability for calcitriol (calcitriol with sevelamer vs calcitriol alone: AUC 137 pg*h/mL vs 318 pg*h/mL and Cmax 40.1 pg/mL vs 49.7 pg/mL, respectively).', NULL, 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', NULL, 'Oral vitamin supplements should be administered at least 4 hours before colesevelam and either 1 hour before or 4 to 6 hours after other bile acid sequestrants and sevelamer.', 'Absorption', 'Beta carotene', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Difelikefalin, Cobicistat, Deferoxamine, Levoleucovorin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109715/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.[4] Peirce D, Hossack S, Poole L, et.al "The effect of sevelamer carbonate and lanthanum carbonate on the pharmacokinetics of oral calcitriol." Nephrol Dial Transplant 26 (2011): 1615-21[5] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[7] "Product Information. Renagel (sevelamer)." Genzyme Corporation, Cambridge, MA.', 1767369485);
