INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109873, 'Zalcitabine', 'Ribavirin', 'Moderate', 'Concomitant use of oral ribavirin and nucleoside reverse transcriptase inhibitors (NRTIs) may increase the risk of symptomatic hyperlactatemia and lactic acidosis. Ribavirin itself is a nucleoside analogue, and these agents alone and in combination have been associated with the syndrome of lactic acidosis and severe hepatomegaly with steatosis. Other mitochondrial toxicities associated with the use of nucleoside analogues include lipodystrophy, peripheral neuropathy, and pancreatitis.', NULL, 'Coadministration of ribavirin and NRTIs should be undertaken with caution and only if the benefit is anticipated to outweigh the potential risks.', NULL, 'Coadministration of ribavirin and NRTIs should be undertaken with caution and only if the benefit is anticipated to outweigh the potential risks. Patients should be advised to promptly seek medical attention if they experience symptoms of toxicity such as nausea, vomiting, abdominal pain/distention, fatigue, unexplained weight loss, tachypnea, dyspnea, motor weakness, numbness, tingling, and pain in hands and feet.', 'Synergism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, More', 'Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/109998/', '[1] Guyader D, Poinsignon Y, Cano Y, Saout L "Fatal lactic acidosis in a HIV-positive patient treated with interferon and ribavirin for chro