INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109881, 'Typhoid vaccine (live)', 'Rifamycin', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Ganciclovir, Acyclovir, Famciclovir, Chlorhexidine, Povidone-iodine, Chlorhexidine, Miconazole, Mupirocin, Nystatin, Amphotericin B, Miconazole, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110006/', '[1] UK government "Typhoid. Available from: URL: https://www.gov.uk/government/uploads/system/uploads/attachment_data/file/148512/Green-Book-Chapter-33-dh_125348.pdf." ([cited 2014]):[2] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] CDC. Centers for Disease Control and Prevention "Typhoid & Paratyphoid Fever. Available from: URL: http://wwwnc.cdc.gov/travel/yellowbook/2014/chapter-3-infectious-diseases-related-to-travel/typhoid-and-paratyphoid-fever." ([2013]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109882, 'Aminophylline', 'Riluzole', 'Minor', 'According to the manufacturer, riluzole serum concentrations may theoretically be increased by concurrent theophylline and aminophylline. The proposed mechanism is metabolic inhibition. The clinical significance of this interaction is not known. The patient should be monitored for altered clinical effect. Dosage adjustments may be necessary if an interaction is suspected.', NULL, '-', NULL, '-', 'Metabolism', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Tetrabenazine, Inotersen, Pitolisant, Deutetrabenazine, Tafamidis', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Reslizumab, Montelukast, Dyphylline, Oxtriphylline, Mepolizumab, Roflumilast, Pentoxifylline, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110007/', '[1] "Product Information. Rilutek (riluzole)." Rhone-Poulenc Rorer, Collegeville, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109883, 'Bedaquiline', 'Riluzole', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', 'Dextromethorphan, Dalfampridine, Sodium oxybate, Amifampridine, Tafamidis', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110008/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (109884, 'Echinacea', 'Riluzole', 'Moderate', 'Coadministration with echinacea may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2. The proposed mechanism is decreased clearance due to echinacea-mediated inhibition of CYP450 1A2.', NULL, 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever echinacea is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Dextromethorphan, Dalfampridine, Sodium oxybate, Tetrabenazine, Inotersen, Pitolisant, Amifampridine, Deutetrabenazine, Tafamidis', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110009/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100', 1767369485);
