INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (110220, 'Risperidone', 'Lithium carbonate', 'Moderate', 'Coadministration of lithium with neuroleptic agents, particularly haloperidol, has been associated with rare cases of an encephalopathic syndrome characterized by weakness, lethargy, fever, tremors, confusion, extrapyramidal symptoms, leukocytosis, and elevated liver enzymes and blood urea nitrogen. This syndrome may be similar to, or the same as, neuroleptic malignant syndrome. Other, more common central nervous system effects may also be increased, such as dizziness, drowsiness, confusion, difficulty concentrating, and impairment in thinking, judgment, and motor coordination.', NULL, 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents.', NULL, 'Close monitoring for central nervous system adverse effects is recommended when lithium is used with neuroleptic agents. Dosage adjustments or discontinuation of one or both drugs may be necessary if an interaction is suspected.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110345/', '[1] Swanson CL, Price WA, Mcevoy JP "Effects of concomitant risperidone and lithium treatment." Am J Psychiatry 152 (1995): 1096[2] Goldman SA "FDA medwatch report: lithium and neuroleptics in combination: the spectrum of neurotoxicity." Psychopharmacol Bull 32 (1996): 299-309[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Ta