        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389711, 'Itraconazole', 'Ciprofloxacin', 'Moderate', 'Coadministration with ciprofloxacin may increase the plasma concentrations of itraconazole. The proposed mechanism is ciprofloxacin inhibition of the CYP450 3A4-mediated metabolism of itraconazole.', NULL, 'Clinical response to itraconazole should be closely monitored during coadministration with ciprofloxacin.', NULL, 'Clinical response to itraconazole should be closely monitored during coadministration with ciprofloxacin. A dosage reduction of itraconazole may be required to minimize the risk of adverse effects such as new onset or worsening congestive heart failure, peripheral or pulmonary edema, hepatotoxicity, peripheral neuropathy, and hearing loss.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
