        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389767, 'Ciprofloxacin', 'Melphalan', 'Minor', 'Limited data suggest that chemotherapy with antineoplastic agents may reduce the plasma concentrations of oral quinolone antibiotics. The proposed mechanism is decreased quinolone absorption secondary to alteration of intestinal mucosa by cancer chemotherapy.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389768, 'Ciprofloxacin', 'Mephenytoin', 'Moderate', 'Ciprofloxacin may increase or decrease plasma phenytoin concentrations during concomitant therapy, increasing the risk of phenytoin toxicity or seizures. In addition, quinolones may lower the seizure threshold. Although not clearly defined, suggested mechanisms for reduced phenytoin concentrations include ciprofloxacin inhibition of phenytoin renal tubular reabsorption resulting in increased phenytoin elimination or an effect by ciprofloxacin on intestinal flora that alters phenytoin enterohepatic recirculation. Other hydantoins may also be affected.', NULL, 'Clinical monitoring of patient response, including serum hydantoin concentrations, is recommended when initiating or discontinuing ciprofloxacin.', NULL, 'Clinical monitoring of patient response, including serum hydantoin concentrations, is recommended when initiating or discontinuing ciprofloxacin. Patients should be advised to notify their physician if they experience a loss of seizure control or possible symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. Adjustment of hydantoin dosages may be required.', 'Synergism, Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
