        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1389943, 'Ciprofloxacin', 'Theophylline', 'Major', 'Coadministration with ciprofloxacin may significantly increase the serum concentrations of theophylline and the associated risk of toxicity. The mechanism is ciprofloxacin inhibition of theophylline metabolism via CYP450 1A2.', NULL, 'The use of theophylline or its salts in combination with ciprofloxacin should generally be avoided.', NULL, 'The use of theophylline or its salts in combination with ciprofloxacin should generally be avoided. If coadministration is required, theophylline dosage may need to be reduced. Pharmacologic response and serum levels should be closely monitored following initiation, discontinuation or change of dosage of ciprofloxacin, and the theophylline dosage adjusted accordingly. Patients should be advised to contact their physician if they experience signs and symptoms suggestive of theophylline toxicity such as nausea, vomiting, diarrhea, anorexia, headache, tremor, irritability, confusion, insomnia, seizure, palpitation, and arrhythmia.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
