INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (110833, 'Tolvaptan', 'Artesunate', 'Moderate', 'Coadministration with tolvaptan may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp). The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by tolvaptan.', NULL, 'Caution is advised when tolvaptan is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', NULL, 'Caution is advised when tolvaptan is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate. Alternatives should be considered whenever possible. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of tolvaptan. The prescribing information for the coadministered drug should be consulted for specific dosing recommendations.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110963/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Jynarque (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[4] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[5] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (110834, 'Tolvaptan', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110964/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (110835, 'Bepridil', 'Tolvaptan', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the oral bioavailability of tolvaptan, which has been shown in vitro to be a substrate of the efflux transporter.', NULL, 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine.', NULL, 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine. Serum sodium concentration and neurologic status should be closely monitored, and the tolvaptan dosage adjusted accordingly. Too rapid correction of hyponatremia increases the risk of osmotic demyelination syndrome, which is associated with dysarthria, mutism, dysphagia, lethargy, affective changes, spastic quadriparesis, seizures, coma, and death. Discontinuation or interruption of tolvaptan therapy should be considered in patients who develop too rapid a rise in serum sodium.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/110965/', '[1] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[3] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (110836, 'Berotralstat', 'Tolvaptan', 'Major', 'Coadministration of berotralstat with drugs that are both inhibitors of the efflux transporters P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) as well as substrates of the isoenzyme CYP450 3A4 may lead to an increase in the plasma concentrations and effects of both drugs. Berotralstat is a substrate of both P-gp and BCRP. Increased plasma concentrations of berotralstat may increase the risk of adverse effects, including the potential for QT prolongation. Berotralstat may cause concentration-dependent prolongation of the Fridericia-corrected QT interval (QTcF). In addition, berotralstat is considered a moderate CYP450 3A4 inhibitor and has been reported in drug interaction studies to increase the peak plasma concentration (Cmax) and systemic exposure (AUC) of sensitive CYP450 3A4 substrate midazolam by approximately 1.5-fold and 2.25-fold, respectively.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with drugs that are both inhibitors of P-gp or BCRP as well as substrates of CYP450 3A4, such as cyclosporine.', NULL, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with drugs that are both inhibitors of P-gp or BCRP as well as substrates of CYP450 3A4, such as cyclosporine. Patients should be advised to contact their physician if they experience any undue adverse effects from their medications. Patients should seek prompt medical attention if they experience symptoms that could indicated the occurrence of torsade de pointes such as dizziness, lightheadedness