INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (111085, 'Trospium', 'Procainamide', 'Moderate', 'Theoretically, coadministration of trospium chloride with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of trospium and/or the coadministered drug(s). The mechanism is competitive inhibition of renal excretion. Drugs that are thought to undergo active tubular secretion include acyclovir/valacyclovir, cidofovir, cimetidine, digoxin, flecainide, ganciclovir/valganciclovir, metformin, midodrine, morphine, pancuronium, procainamide, quinidine, ranitidine, tenofovir, triamterene, and vancomycin.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving trospium chloride in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Tolterodine, Phenazopyridine, Sildenafil, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, More', 'Amiodarone, Dronedarone, Tocainide, Lidocaine, Propafenone, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111215/', '[1] "Product Information. Sanctura (trospium chloride)." Odyssey Pharmaceutical, East Hanover, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.