INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (111267, 'Teriflunomide', 'Sitagliptin', 'Moderate', 'Coadministration with teriflunomide may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of organic anion transporter 3 (OAT3). The proposed mechanism, based on in vivo data, is decreased clearance due to teriflunomide-mediated inhibition of OAT3 transporter.', NULL, 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OAT3.', NULL, 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OAT3. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever teriflunomide is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Dulaglutide, Exenatide, Tolbutamide, Miglitol, Glimepiride, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/111397/', '[1] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
