INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (111913, 'Velpatasvir', 'Ripretinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of ripretinib and its active metabolite, DP-5439. In vitro studies indicate that ripretinib and DP-5439 are substrates of the P-gp efflux transporter.', NULL, 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp.', NULL, 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp. Clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary. Patients should be monitored for development of adverse effects such as nausea, vomiting, constipation, diarrhea, alopecia, arthralgia, myalgia, hypertension, cardiac dysfunction, and palmar-plantar erythrodysesthesia.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112044/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Qinlock (ripretinib)." Deciphera Pharmaceuticals, Waltham, MA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Qinlock (ripretinib)." Deciphera Pharmaceuticals, Waltham, MA.[5] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.[6] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[7] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice