INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (111933, 'Voxilaprevir', 'Atazanavir', 'Major', 'Coadministration with atazanavir/ritonavir or lopinavir/ritonavir may significantly increase the plasma concentrations of voxilaprevir. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of voxilaprevir by atazanavir, lopinavir, and ritonavir. Inhibition of P-glycoprotein-mediated intestinal efflux and CYP450 3A4-mediated metabolism of voxilaprevir may also contribute.', NULL, 'Concomitant use of sofosbuvir/velpatasvir/voxilaprevir with atazanavir- or lopinavir-containing regimens is not recommended.', NULL, 'Concomitant use of sofosbuvir/velpatasvir/voxilaprevir with atazanavir- or lopinavir-containing regimens is not recommended.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112064/', '[1] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences, Foster City, CA.[2] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences, Foster City, CA.[3] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences, Foster City, CA.', 1767363023);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (111934, 'Voxilaprevir', 'Atorvastatin', 'Moderate', 'Coadministration with sofosbuvir/velpatasvir/voxilaprevir may increase the plasma concentrations of all statins including atorvastatin, fluvastatin, lovastatin, and simvastatin. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of statins by velpatasvir and voxilaprevir. Although the interaction has not been studied specifically with atorvastatin, fluvastatin, lovastatin or simvastatin, it has been reported with other statins that are also substrates of OATP1B1. High levels of HMG-CoA reductase inhibitory activity in plasma may be associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir.', NULL, 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir. The lowest approved dosage of the statin should be used. If higher dosages are required, the lowest effective statin dosage should be prescribed based on a risk/benefit assessment.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112065/', '[1] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences, Foster City, CA.[2] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences, Foster City, CA.[3] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences, Foster City, CA.', 1767363023);
