INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112001, 'Gadobenic acid', 'Tamoxifen', 'Moderate', 'Gadobenate dimeglumine may compete with other substrates of the canalicular multispecific organic anion transporter, also known as cMOAT or MRP2, which is an endogenous transporter protein that works in coordination with the conjugation process to facilitate drug excretion. In solid tumor cells, overexpression of MRP2 confers resistance to a wide variety of anticancer chemotherapeutic agents. Substrates of MRP2 include anthracyclines, protease inhibitors, taxanes, vinca alkaloids, cisplatin, etoposide, methotrexate, and tamoxifen. Theoretically, competition for MRP2 transport may interfere with the clearance of these drugs and/or gadobenate dimeglumine.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2.', NULL, 'Caution is advised during concomitant use of gadobenate dimeglumine and other substrates of MRP2. Patients should be monitored for potentially increased pharmacologic effects of gadobenate dimeglumine as well as the coadministered drug(s).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112132/', '[1] "Product Information. Multihance (gadobenate dimeglumine)." Bracco Diagnostics Inc, Princeton, NJ.[2] Huisman MT, Smit JW, Crommentuyn KM, et al. "Multidrug resistance protein 2 (MRP2) transports HIV protease inhibitors, and transport can be enhanced by other drugs." AIDS 16 (2002): 2295-2301', 1767365206);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112002, 'Galantamine', 'Tamoxifen', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112133/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365206);
