INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112160, 'Tamoxifen', 'Sparfloxacin', 'Major', 'Sparfloxacin may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of sparfloxacin with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of sparfloxacin with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112291/', '[1] Thomas M, Maconochie JG, Fletcher E "The dilemma of the prolonged QT interval in early drug studies." Br J Clin Pharmacol 41 (1996): 77-81[2] Jaillon P, Morganroth J, Brumpt I, Talbot G "Overview of electrocardiographic and cardiovascular safety data for sparfloxacin. Sparfloxacin Safety Group." J Antimicrob Chemother 37(suppl a) (1996): 161-7[3] Dupont H, Timsit JF, Souweine B, Gachot B, Wolff M, Regnier B "Torsades de pointe probably related to sparfloxacin." Eur J Clin Microbiol Infect Dis 15 (1996): 350-1[4] Lipsky BA, Dorr MB, Magner DJ, Talbot GH "Safety profile of sparfloxacin, a new fluoroquinolone antibiotic." Clin Ther 21 (1999): 148-59[5] Owens RC Jr, Ambrose PG "Torsades de pointes associated with fluoroquinolones." Pharmacotherapy 22 (2002): 663-8; discussion 668-72[6] Owens RC "QT Prolongation with Antimicrobial Agents : Understanding the Significance." Drugs 64 (2004): 1091-124[7] Zix JA, GeerdesFenge HF, Rau M, Vockler J, Borner K, Koep