INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112164, 'Tamoxifen', 'Sulfinpyrazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tamoxifen, which is metabolized by the isoenzyme.', NULL, 'In general, agents without CYP450 3A4-inducing activity are preferable in patients treated with tamoxifen, particularly if these agents are to be used for a prolonged period.', NULL, 'In general, agents without CYP450 3A4-inducing activity are preferable in patients treated with tamoxifen, particularly if these agents are to be used for a prolonged period. Otherwise, pharmacologic response to tamoxifen should be monitored closely whenever an inducing agent is added to or withdrawn from therapy, and the tamoxifen dosage adjusted if necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112295/', '[1] Biochem Pharmacol "Variable contribution of cytochromes P450 2D6, 2C9, and 3A4 to the 4-hydroxylation of tamoxifen by human liver microsomes." Biochem Pharmacol 53 (1997): 171-8[2] Lien EA, Anker G, Lonning PE, et al "Decreased serum concentrations of tamoxifen and its metabolites induced by aminoglutethimide." Cancer Res 50 (1990): 5851-7[3] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[4] Kivisto KT, Villikka K, Nyman L, Anttila M, Neuvonen PJ "Tamoxifen and toremifene concentrations in plasma are greatly decreased by rifampin." Clin Pharmacol Ther 64 (1998): 648-54[5] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[6] Skillman TG, Feldman JM "The pharmacol