        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1390912, 'Enoxacin', 'Potassium citrate', 'Moderate', 'Urinary alkalinizers such as citrates may decrease the solubility of fluoroquinolones in the urine and increase the risk of crystalluria.', NULL, 'Concomitant use should generally be avoided.', NULL, 'Concomitant use should generally be avoided. Patients receiving this combination should be monitored for signs of renal toxicity and crystalluria.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1390913, 'Ephedrine (nasal)', 'Potassium citrate', 'Moderate', 'Alkalization of the urine decreases the urinary excretion increases the elimination half-life of ephedrine, pseudoephedrine, and related drugs. Toxicity from long-term use of pseudoephedrine has been demonstrated in patients with persistently alkaline urine.', NULL, 'Patients should be monitored for toxic effects such as tremor, anxiety, insomnia, irritability, or nervousness.', NULL, 'Patients should be monitored for toxic effects such as tremor, anxiety, insomnia, irritability, or nervousness. Dosage reductions may be required.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
