        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1391371, 'Efavirenz', 'Clarithromycin', 'Moderate', 'Efavirenz has been shown to decrease the area under the plasma concentration-time curve of clarithromycin (500 mg every 12 hours) by 39%. This interaction is attributable to the enhanced metabolism of clarithromycin caused by efavirenz.', NULL, 'The manufacturer recommends that alternative therapy such as azithromycin be considered.', NULL, 'The manufacturer recommends that alternative therapy such as azithromycin be considered. No dosage adjustments are recommended. If concomitant therapy cannot be avoided, the patient should be monitored for decreased therapeutic effect of clarithromycin.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
