        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1391382, 'Eplerenone', 'Clarithromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and risk of adverse reactions of eplerenone, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of eplerenone with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of eplerenone with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of eplerenone and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
