        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1391392, 'Esomeprazole', 'Clarithromycin', 'Minor', 'Clarithromycin may increase the plasma concentration of esomeprazole. The mechanism may be related to clarithromycin inhibition of hepatic cytochrome P450 enzymes responsible for esomeprazole metabolism. The systemic exposure (AUC) to esomeprazole doubled after coadministration with clarithromycin 500 mg twice daily. The clinical significance is unknown and dose adjustments are not required.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
