        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1391402, 'Etravirine', 'Clarithromycin', 'Moderate', 'Coadministration with etravirine may decrease the plasma concentrations of clarithromycin and increase concentrations of the active metabolite, 14-hydroxy-clarithromycin. The mechanism is etravirine induction of CYP450 3A4, the isoenzyme that metabolizes clarithromycin to 14-hydroxy-clarithromycin.', NULL, 'Because 14-hydroxy-clarithromycin has demonstrated reduced activity relative to the parent drug against Mycobacterium avium complex (MAC), overall activity against this pathogen may be altered during coadministration of clarithromycin with etravirine.', NULL, 'Because 14-hydroxy-clarithromycin has demonstrated reduced activity relative to the parent drug against Mycobacterium avium complex (MAC), overall activity against this pathogen may be altered during coadministration of clarithromycin with etravirine. Therefore, alternatives to clarithromycin (e.g., azithromycin) should be considered for the treatment of MAC in patients receiving etravirine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
