    ) VALUES (1391414, 'Fluconazole', 'Clarithromycin', 'Minor', 'Fluconazole may increase plasma concentrations of clarithromycin when coadministered. The mechanism of the interaction is unknown. Observation for clarithromycin adverse effects is recommended if these drugs are given together.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
