        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1391474, 'Itraconazole', 'Clarithromycin', 'Moderate', 'Coadministration of itraconazole and the macrolide antibiotics may result in elevated plasma concentrations of itraconazole. The mechanism is decreased clearance of itraconazole due to competitive inhibition of CYP450 3A4 enzymatic activity by the macrolide agents.', NULL, 'Caution is advised if itraconazole must be used concomitantly with erythromycin or clarithromycin.', NULL, 'Caution is advised if itraconazole must be used concomitantly with erythromycin or clarithromycin. Patients should be advised to notify their physician if they experience possible symptoms of itraconazole toxicity, such as nausea, vomiting, diarrhea, abdominal pain, or headache. Alternative macrolide antibiotics such as azithromycin or dirithromycin, which do not inhibit CYP450, may be appropriate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
