    ) VALUES (1391475, 'Ivabradine', 'Clarithromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ivabradine with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of ivabradine with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of ivabradine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
