INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112523, 'Vardenafil', 'Phenolphthalein', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Darifenacin, Tadalafil, Ammonium chloride, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, More', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Tegaserod, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112654/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112524, 'Vardenafil', 'Phenoxybenzamine', 'Moderate', 'Vardenafil may potentiate the hypotensive effect of alpha blockers, resulting in symptomatic hypotension in some patients. Vardenafil inhibits phosphodiesterase-5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation that may be additive with that induced by alpha blockers.', NULL, 'Caution is advised if vardenafil is used in combination with alpha blockers.', NULL, 'Caution is advised if vardenafil is used in combination with alpha blockers. Patients who demonstrate hemodynamic instability on alpha blocker therapy alone are at increased risk of symptomatic hypotension with concomitant use of vardenafil. Therefore, patients should be stable on alpha blocker therapy prior to initiating vardenafil, and vardenafil should be initiated at the lowest recommended dosage (i.e., 5 mg). In those patients already on an optimized dosage of vardenafil, alpha blocker therapy should be initiated at the lowest dosage. The safety of vardenafil in combination with alpha blockers may be affected by other variables such as intravascular volume depletion and use of other antihypertensive medications.', 'Synergism', 'Darifenacin, Ammonium chloride, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, Magnesium hydroxide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112655/', '[1] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[2] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juic