INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112528, 'Vardenafil', 'Pindolol', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, Ammonium chloride, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112659/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112529, 'Vardenafil', 'Pitolisant', 'Moderate', 'Limited data suggest that pitolisant may produce mild to moderate QT interval prolongation (10 to 13 milliseconds) at doses 3 to 6 times the standard therapeutic dose. Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Coadministration with pitolisant may decrease the plasma concentrations and therapeutic effects of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4. When studied with midazolam, a probe substrate for CYP450 3A4, pitolisant was found to reduce midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) by less than 25%.', NULL, 'Concomitant use of pitolisant with other agents associated with QT interval prolongation should generally be avoided.', NULL, 'Concomitant use of pitolisant with other agents associated with QT interval prolongation should generally be avoided. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. In addition, it may be advisable to avoid concomitant use of pitolisant with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index, if possible (e.g., ergot alkaloids, alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, macrolide immunosuppressants, oral midazolam, pimozide, quinidine, sufentanil, triazolam, vinca alkaloids). Caution is advised if pitolisant is used in combination with these drugs. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary.', 'Metabolism', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Sildenafil, Trospium, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, More', 'Dextromethorphan, Dalfampridine, Sodium oxybate, Riluzole, Inotersen, Amifampridine, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112660/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.[3] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[4] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112530, 'Vardenafil', 'Polyethylene glycol (3350)', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Darifenacin, Tadalafil, Ammonium chloride, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112661/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian 