INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112567, 'Vardenafil', 'Simeprevir', 'Moderate', 'Coadministration with simeprevir may result in mild increases in plasma concentrations of phosphodiesterase-5 (PDE5) inhibitors, which are primarily metabolized by CYP450 3A4. In vivo, simeprevir mildly inhibits intestinal CYP450 3A4, but does not affect hepatic CYP450 3A4 activity. Therefore, a major pharmacokinetic interaction is not expected under normal circumstances.', NULL, 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction.', NULL, 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction. However, dosage adjustment of sildenafil or tadalafil may be needed when these agents are administered chronically for the treatment of pulmonary arterial hypertension. Consider starting with the lowest effective dose and increase as needed, with clinical monitoring as appropriate.', 'Metabolism', 'Darifenacin, Ammonium chloride, Apomorphine, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, More', 'Tinidazole, Ascorbic acid, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, Probenecid, Tioconazole, Diiodohydroxyquinoline, Oxytetracycline, Zonisamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112698/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.[4] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.', 1767369485);
