INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112585, 'Vardenafil', 'Telaprevir', 'Major', 'Coadministration with telaprevir may significantly increase the plasma concentrations of vardenafil. The mechanism involves inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of vardenafil. The interaction has not been specifically studied with telaprevir, but has been reported with other potent CYP450 3A4 inhibitors such as HIV protease inhibitors, ketoconazole, and erythromycin.', NULL, 'Patients treated with telaprevir should not receive more than a single 2.', NULL, 'Patients treated with telaprevir should not receive more than a single 2.5 mg dose of vardenafil within a 72-hour period. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, Remdesivir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112716/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[3] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[4] Fuhr U, Maier-Bruggemann A,