INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112587, 'Vardenafil', 'Telithromycin', 'Moderate', 'Telithromycin has the potential to prolong the QT interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if telithromycin is used in combination with other drugs that can prolong the QT interval.', NULL, 'Although the risk of a serious interaction is probably low, caution is recommended if telithromycin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation may increase with increasing plasma concentrations of telithromycin, the recommended dosage should not be exceeded, particularly in patients with renal or hepatic impairment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Phenyl salicylate, Ammonium chloride, Dimethyl sulfoxide', 'Dirithromycin, Lincomycin, Clindamycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112718/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Ketek (telithromycin)." Aventis Pharmaceuticals, Bridgewater, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Demolis JL, Vacheron F, Cardus S, Funck-Brentano C "Effect of single and repeated oral doses of telithromycin on cardiac QT interval in healthy subjects." Clin Pharmacol Ther 73 (2003): 242-52[5] European Agency for the Evaluation of Medicinal Products. Committee for Proprietary Medicinal Products "European Public Assessment Report Ketek (telithromycin) (Rev. 2) Available from: URL: http:www.emea.eu.int/humandocs/Humans/EPAR/Ketek/Ketek.htm." ([2001]):[6] Cerner Multum, Inc. "Australian Product Information." O 0[7] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[8] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112588, 'Vardenafil', 'Telmisartan', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Fesoterodine, Pentosan polysulfate, Phenazopyridine, Alprostadil, Trospium, Mirabegron, Acetohydroxamic acid, Yohimbine, Flavoxate, Darifenacin, Papaverine, More', 'Sacubitril', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112719/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112589, 'Vardenafil', 'Terazosin', 'Moderate', 'Vardenafil may potentiate the hypotensive effect of alpha blockers, resulting in symptomatic hypotension in some patients. Vardenafil inhibits phosphodiesterase-5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation that may be additive with that induced by alpha blockers.', NULL, 'Caution is advised if vardenafil is used in combination with alpha blockers.', NULL, 'Caution is advised if vardenafil is used in combination with alpha blockers. Patients who demonstrate hemodynamic instability on alpha blocker therapy alone are at increased risk of symptomatic hypotension with concomitant use of vardenafil. Therefore, patients should be stable on alpha blocker therapy prior to initiating vardenafil, and vardenafil should be initiated at the lowest recommended dosage (i.e., 5 mg). In those patients already on an optimized dosage of vardenafil, alpha blocker therapy should be initiated at the lowest dosage. The safety of vardenafil in combination with alpha blockers may be affected by other variables such as intravascular volume depletion and use of other antihypertensive medications.', 'Synergism', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Mirabegron, Acetohydroxamic acid, Yohimbine, Flavoxate, Darifenacin, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112720/', '[1] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[2] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med