INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112704, 'Idelalisib', 'Sufentanil', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of sufentanil, which is primarily metabolized by the isoenzyme. Increased sufentanil concentrations may enhance or prolong pharmacologic effects and potentiate the risk of central nervous system and respiratory depression.', NULL, 'Patients receiving sufentanil with potent CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary.', NULL, 'Patients receiving sufentanil with potent CYP450 3A4 inhibitors should be carefully monitored for excessive central nervous system and respiratory depression, and dosage adjustments made accordingly if necessary.', 'Metabolism', 'Thiopental, Remifentanil, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, Sevoflurane, Etomidate', 'Entrectinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Bosutinib, Vemurafenib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112835/', '[1] Bartkowski RR, Goldberg ME, Huffnagle S, Epstein RH "Sufentanil disposition. Is it affected by erythromycin administration?" Anesthesiology 78 (1993): 260-5[2] "Product Information. Sufenta (sufentanil)." Janssen Pharmaceutica, Titusville, NJ.[3] Tateishi T, Krivoruk Y, Ueng YF, Wood AJ, Guengerich FP, Wood M "Identification of human cytochrome P-450 3A4 as the enzyme responsible for fentanyl and sufentanil N-dealkylation." Anesth Analg 82 (1996): 167-72[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112705, 'Ifosfamide', 'Sufentanil', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Halothane, Isoflurane, Nitrous oxide, Methohexital, Desflurane, Propofol, Enflurane, Sevoflurane, Etomidate, Ketamine', 'Temozolomide, Thiotepa, Carmustine, Cyclophosphamide, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/112836/', '[1] Simonian NA, Gilliam FG, Chiappa KH "Ifosfamide causes a diazepam-sensitive 