INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112885, 'Ticagrelor', 'Sufentanil', 'Moderate', 'Coadministration with ticagrelor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by ticagrelor, which is a mild inhibitor. The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Concomitant use of ticagrelor with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Concomitant use of ticagrelor with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., cisapride, ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, pimozide, triazolam, vinca alkaloids) is not recommended.', 'Metabolism', 'Halothane, Thiopental, Sodium oxybate, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, Esketamine, Sevoflurane, More', 'Betrixaban, Apixaban, Cilostazol, Iloprost, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113016/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112886, 'Timolol', 'Sufentanil', 'Moderate', 'In patients receiving chronic beta-blocker therapy, the use of sufentanil may lead to a higher degree of bradycardia and hypotension. One study has shown that induction and maintenance of anesthesia requires lower doses of sufentanil in patients receiving beta-blockers.', NULL, 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', NULL, 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', 'Synergism', 'Latanoprost, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, More', 'Halothane, Thiopental, Isoflurane, Methohexital, Nitrous oxide, Desflurane, Enflurane, Esketamine, Sevoflurane, Etomidate, Ketamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113017/', '[1] Thomson IR, MacAdams CL, Hudson RJ, Rosenbloom M "Drug interactions with sufentanil: hemodynamic effects of premedication and muscle relaxants." Anesthesiology 76 (1992): 922-9[2] "Product Information. Sufenta (sufentanil)." Janssen Pharmaceutica, Titusville, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112887, 'Tizanidine', 'Sufentanil', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'Isoflurane, Nitrous oxide, Methohexital, Desflurane, Enflurane, Etomidate, Ketamine', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113018/', '[1] US Food and Drug Administration "FDA warns about serious risks and death when combining opioid pain or cough medicines with benzodiazepines; requires its strongest warning. Available from: URL: http://www.fda.gov/downloads/Drugs/DrugSafety/UCM518672.pdf." ([2016, Aug 31]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112888, 'Tocilizumab', 'Sufentanil', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Halothane, Thiopental, Alfentanil, Remifentanil, Isoflurane, Sodium oxybate, Methohexital, Nitrous oxide, Desflurane, Propofol, Enflurane, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, Abatacept, Upadacitinib, Siponimod, Apremilast, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113019/', '[1] "Product Information. Skyrizi (risankizumab)." AbbVie US LLC, North Chicago, IL.[2] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Gamifant (emapalumab)." Sobi Inc, Ardmore, PA.[5] "Product Information. Kevzara (sarilumab)." sanofi-aventis, Bridgewater, NJ.[6] "Product Information. Simponi (golimumab)." Centocor Inc, Malvern, PA.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[8] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[9] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[10] "Product Information. Ilumya (tildrakizumab)." Merck & Company Inc, Whitehouse Station, NJ.[11] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[12] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[13] "Product Information. Remicade (infliximab)." Centocor Inc, Malvern, PA.[14] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[15] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[16] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[17] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of 