INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (112936, 'Dicoumarol', 'Sulfamethoxazole', 'Major', 'Coadministration with a sulfonamide may increase the plasma concentrations and hypoprothrombinemic effects of coumarin anticoagulants. The proposed mechanism is sulfonamide inhibition of coumarin metabolism via CYP450 2C9 and possibly also displacement of coumarin derivatives from plasma protein binding sites.', NULL, 'Patients receiving coumarin anticoagulants should be closely monitored during concomitant therapy with sulfonamides.', NULL, 'Patients receiving coumarin anticoagulants should be closely monitored during concomitant therapy with sulfonamides. The INR should be checked frequently and coumarin dosage adjusted accordingly, particularly following initiation or discontinuation of sulfonamide therapy in patients who are stabilized on their anticoagulant regimen. The same precaution may be applicable during therapy with other oral anticoagulants (e.g., indandiones), although clinical data are lacking. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Metabolism, Distribution', 'Ticagrelor, Betrixaban, Clopidogrel, Apixaban, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Rifapentine, Streptomycin, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 