INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113035, 'Trimethoprim', 'Montelukast', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 2C9 may increase the plasma concentrations of montelukast, which is metabolized by these isoenzymes in addition to CYP450 3A4. When montelukast was administered with the potent CYP450 2C8/2C9 inhibitor gemfibrozil, montelukast systemic exposure (AUC) increased by 4.4-fold.', NULL, 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil.', NULL, 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil. However, it may be advisable to monitor patients for potentially increased adverse effects during coadministration with gemfibrozil or other CYP450 2C8/2C9 inhibitors.', 'Metabolism', 'Sulfisoxazole, Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Streptomycin, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Theophylline, Omalizumab, Tezepelumab, Aminophylline, Benralizumab, Caffeine, Reslizumab, Dyphylline, Oxtriphylline, Mepolizumab, Roflumilast, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113166/', '[1] Karonen T, Filppula A, Laitila J, Niemi M, Neuvonen PJ, Backman JT "Gemfibrozil Markedly Increases the Plasma Concentrations of Montelukast: A Previously Unrecognized Role for CYP2C8 in the Metabolism of Montelukast." Clin Pharmacol Ther (2010):[2] "Product Information. Singulair (montelukast)." Merck & Co, Inc, West Point, PA.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767