INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113080, 'Trimethoprim', 'Pralatrexate', 'Moderate', 'Coadministration with drugs that are subject to substantial renal clearance such as trimethoprim/sulfamethoxazole and nonsteroidal anti-inflammatory drugs (NSAIDs) may result in delayed clearance of pralatrexate. The mechanism is competitive inhibition of renal excretion, as approximately 34% of pralatrexate is eliminated unchanged by the kidney.', NULL, 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs.', NULL, 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs. Complete blood cell counts as well as renal and hepatic function should be closely monitored. Treatment interruption or dose reduction of pralatrexate may be required if toxicities such as mucositis, myelosuppression, or liver enzyme abnormalities develop. Patients should be advised to contact their physician if they develop oral ulcers, painful swallowing, vomiting, diarrhea, and/or signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Excretion', 'Sulfadiazine, Sulfamethoxazole, Sulfamethizole, Sulfisoxazole, Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Rifampicin, More', 'Fluorouracil, Pemetrexed, Cladribine, Clofarabine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Decitabine, Azacitidine, Tioguanine, Capecitabine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113211/', '[1] "Product Information. Folotyn (pralatrexate)." Allos Therapeutics, Westminster, CO.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113081, 'Sulfamethoxazole', 'Primaquine', 'Minor', 'Limited data suggest that sulfamethoxazole-trimethoprim (SMX-TMP) may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with SMX-TMP intravenously. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', NULL, '-', NULL, '-', 'Synergism', 'Capreomycin, Pyrazinamide, Rifamycin, Rifapentine, Streptomycin, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 'Artesunate, Artemether, Tafenoquine, Proguanil, Sulfadoxine', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113212/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Crouch MA, Limon L, Cassano AT "Clinical relevance and management of drug-related QT interval prolongation." Pharmacotherapy 23 (2003): 881-908[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Wiener I, Rubin D, Martinez E, et al "QT prolongation and paroxysmal ventricular tachycardia occurring during fever following trimethoprim-sulfamethoxazole administration." Mt Sinai J Med 48 (1981): 53-5[5] Lopez JA, Harold JG, Rosenthal MC, Oseran DS, Schapira JN, Peter T "QT prolongation and torsades de pointes after administration of trimethoprin-sulfamethoxazole." Am J Cardiol 59 (1987): 376-7[6] Darpo B "Spectrum of drugs prolonging QT interval and the incidence of torsades de pointes." Eur Heart J Suppl 3(Suppl K) (2001): K70-80[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113082, 'Trimethoprim', 'Primidone', 'Moderate', 'Coadministration with potent inducers of CYP450 2C9 and 3A4 may decrease the plasma concentrations of sulfamethoxazole and trimethoprim. A small amount of sulfamethoxazole is metabolized by CYP450 2C9, while trimethoprim is partially metabolized by both CYP450 2C9 and 3A4.', NULL, 'The potential for diminished pharmacologic effects of sulfamethoxazole-trimethoprim should be considered during coadministration with potent CYP450 2C9 ad 3A4 inducers, particularly when sulfamethoxazole-trimethoprim is used for prevention of toxoplasmic encephalitis in HIV patients because adequate drug levels are required in the central nervous system.', NULL, 'The potential for diminished pharmacologic effects of sulfamethoxazole-trimethoprim should be considered during coadministration with potent CYP450 2C9 ad 3A4 inducers, particularly when sulfamethoxazole-trimethoprim is used for prevention of toxoplasmic encephalitis in HIV patients because adequate drug levels are required in the central nervous system. It may be advisable to refrain from low-dose prophylactic regimens (less than 4 DS tablets per week). Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Sulfadiazine, Sulfamethoxazole, Sulfamethizole, Sulfisoxazole, Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Cycloserine, More', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Methylphenobarbital, Pentobarbital, Zaleplon, Dichloralphenazone, Valerian, Butabarbital, Secobarbital, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113213/', '[1] Rib