INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113098, 'Sulfamethoxazole', 'Rifampicin', 'Moderate', 'During coadministration of rifampin and sulfamethoxazole-trimethoprim (SMX-TMP), the serum concentrations of rifampin may be increased while those of SMX and TMP may be decreased.', NULL, 'Patients receiving rifampin and SMX-TMP should be monitored for potentially increased toxicity of rifampin.', NULL, 'Patients receiving rifampin and SMX-TMP should be monitored for potentially increased toxicity of rifampin. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, the possibility of reduced efficacy of SMX-TMP should be considered, particularly when used for prevention of toxoplasmic encephalitis in HIV patients because adequate drug levels are required in the central nervous system. It may be advisable to refrain from low-dose prophylactic regimens (less than 4 DS tablets per week).', 'Others', 'Capreomycin, Rifamycin, Rifapentine, Rifampicin, Cycloserine, Rifabutin, Pyridoxine, Sulfadiazine, Sulfamethizole, Sulfisoxazole, Ascorbic acid, More', 'Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113229/', '[1] Ribera E, Pou L, Fernandez-Sola A, et al. "Rifampin reduces concentrations of trimethoprim and sulfamethoxazole in serum in human immunodeficiency virus-infected patients." Antimicrob Agents Chemother 45 (2001): 3238-41[2] Ribera E, FernandezSola A, Juste C, Rovira A, Romero FJ, ArmadansGil L, Ruiz I, Ocana I, Pahissa A "Comparison of high and low doses of trimethoprim-sulfamethoxazole for primary prevention of toxoplasmic encephalitis in human immunodeficiency virus-infected patients." Clin Infect Dis 29 (1999): 1461-6[3] Bhatia RS, Uppal R, Malhi R, Behera D, Jindal SK "Drug interaction between rifampicin and cotrimazole in patients with tuberculosis." Hum Exp Toxicol 10 (1991): 419-21', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113099, 'Trimethoprim', 'Rifapentine', 'Moderate', 'Coadministration with potent inducers of CYP450 2C9 and 3A4 may decrease the plasma concentrations of sulfamethoxazole and trimethoprim. A small amount of sulfamethoxazole is metabolized by CYP450 2C9, while trimethoprim is partially metabolized by both CYP450 2C9 and 3A4.', NULL, 'The potential for diminished pharmacologic effects of sulfamethoxazole-trimethoprim should be considered during coadministration with potent CYP450 2C9 ad 3A4 inducers, particularly when sulfamethoxazole-trimethoprim is used for prevention of toxoplasmic encephalitis in HIV patients because adequate drug levels are required in the central nervous system.', NULL, 'The potential for diminished pharmacologic effects of sulfamethoxazole-trimethoprim should be considered during coadministration with potent CYP450 2C9 ad 3A4 inducers, particularly when sulfamethoxazole-trimethoprim is used for prevention of toxoplasmic encephalitis in HIV patients because adequate drug levels are required in the central nervous system. It may be advisable to refrain from low-dose prophylactic regimens (less than 4 DS tablets per week). Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Sulfadiazine, Sulfamethoxazole, Sulfamethizole, Sulfisoxazole, Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Rifampicin, More', 'Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, Pyridoxine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113230/', '[1] Ribera E, Po