INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113133, 'Trabectedin', 'Sulfamethoxazole', 'Moderate', 'Coadministration of trabectedin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', 'Capreomycin, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Pyridoxine, Pretomanid, Trimethoprim, Tamsulosin, Ascorbic acid, Hydroflumethiazide, More', 'Vincristine, Cabazitaxel, Vinblastine, Paclitaxel, Topotecan, Teniposide, Irinotecan, Vinorelbine, Etoposide, Docetaxel', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113264/', '[1] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113134, 'Trimethoprim', 'Trandolapril', 'Major', 'The use of trimethoprim in combination with other potassium-sparing drugs or potassium salts may increase the risk of hyperkalemia. Trimethoprim inhibits sodium reabsorption and potassium excretion by blocking sodium channels in the renal distal tubules.', NULL, 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration.', NULL, 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. A dosage reduction of trimethoprim is recommended in renal dysfunction (50% reduction for CrCl between 15 and 30 mL/min). Patients should be given dietary counseling to avoid excessive intake of potassium-rich foods and salt substitutes, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat. Trimethoprim should be discontinued if hyperkalemia occurs.', 'Excretion', 'Sulfadiazine, Sulfamethoxazole, Sulfamethizole, Sulfisoxazole, Cap