INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113186, 'Nitisinone', 'Naproxen', 'Moderate', 'Coadministration with nitisinone may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2C9, such as warfarin and phenytoin. The proposed mechanism is decreased clearance due to nitisinone-mediated inhibition of CYP450 2C9 isoenzyme.', NULL, 'If nitisinone is used concomitantly with sensitive CYP450 2C9 substrates (e.', NULL, 'If nitisinone is used concomitantly with sensitive CYP450 2C9 substrates (e.g., celecoxib, tolbutamide) or CYP450 2C9 substrates with a narrow therapeutic index (e.g., phenytoin, warfarin), the dosage of the drug metabolized by CYP450 2C9 should be reduced by one-half and additional dosage adjustments made as needed to maintain therapeutic drug concentrations. Clinical and laboratory monitoring should be considered whenever nitisinone is added to or withdrawn from therapy with CYP450 2C9 substrates. Refer to individual substrate prescribing information for additional dosing recommendations.', 'Metabolism', 'Teduglutide, Lonafarnib, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, More', 'Ritodrine, Bremelanotide, Black cohosh, Cabergoline, Rabeprazole, Tolazoline, Ketoprofen, Tolmetin, Meclofenamic acid, Dimethyl sulfoxide, Fenoprofen, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113317/', '[1] "Product Information. Orfadin (nitisinone)." Orphan Pharmaceuticals USA, Inc, Nashville, TN.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subsc