INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113193, 'Pitolisant', 'Naproxen', 'Moderate', 'Concomitant use of pitolisant with NSAIDs and/or corticosteroids may increase the risk of gastrointestinal adverse effects such as dyspepsia, abdominal pain or discomfort, and gastritis due to potential additive irritant effects on the gastrointestinal mucosa. However, the effects were described as mostly mild to moderate in severity. Data are not available on the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients.', NULL, 'Caution is advised if pitolisant is used in combination with NSAIDs and/or corticosteroids, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly or debilitated patients. If concomitant therapy is required, patients should be advised to report signs and symptoms of adverse GI effects, including abdominal pain or discomfort, dyspepsia, gastroesophageal reflux disease, gastritis, or the appearance of black, tarry stools.', 'Synergism', 'Bremelanotide, Black cohosh, Flibanserin, Cabergoline, Tolazoline, Rabeprazole, Dimethyl sulfoxide, Rabeprazole, Misoprostol, Glucosamine, Chondroitin sulfate, More', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Amifampridine, Deutetrabenazine, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113324/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113194, 'Plazomicin', 'Naproxen', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Ritodrine, Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Tolazoline, Dimethyl sulfoxide, Misoprostol, Chondroitin sulfate, Glucosamine', 'Neomycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113325/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113195, 'Pralatrexate', 'Naproxen', 'Moderate', 'Coadministration with drugs that are subject to substantial renal clearance such as trimethoprim/sulfamethoxazole and nonsteroidal anti-inflammatory drugs (NSAIDs) may result in delayed clearance of pralatrexate. The mechanism is competitive inhibition of renal excretion, as approximately 34% of pralatrexate is eliminated unchanged by the kidney.', NULL, 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs.', NULL, 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs. Complete blood cell counts as well as renal and hepatic function should be closely monitored. Treatment interruption or dose reduction of pralatrexate may be required if toxicities such as mucositis, myelosuppression, or liver enzyme abnormalities develop. Patients should be advised to contact their physician if they develop oral ulcers, painful swallowing, vomiting, diarrhea, and/or signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Excretion', 'Ritodrine, Bremelanotide, Flibanserin, Cabergoline, Bromocriptine, Tolazoline, Rabeprazole, Dimethyl sulfoxide, Rabeprazole, Misoprostol, Glucosamine, More', 'Cladribine, Trifluridine, Floxuridine, Nelarabine, Cytarabine, Decitabine, Azacitidine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113326/', '[1] "Product Information. Folotyn (pralatrexate)." Allos Therapeutics, Westminster, CO.', 1767369485);
