INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113199, 'Ruxolitinib', 'Naproxen', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Ritodrine, Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Tolazoline, Rabeprazole, Dimethyl sulfoxide, Rabeprazole, Misoprostol, More', 'Entrectinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Bosutinib, Selumetinib, Ribociclib, Vandetanib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113330/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113200, 'Selpercatinib', 'Naproxen', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 2C8 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 2C8 by selpercatinib.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when selpercatinib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Bremelanotide, Ibuprofen, Cabergoline, Indomethacin, Tolazoline, Ketoprofen, Diclofenac, Piroxicam, Flurbiprofen, Ibuprofen, Tolmetin, More', 'Entrectinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Larotrectinib, Bosutinib, Selumetinib, Ribociclib, Vandetanib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113331/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113201, 'Sodium bicarbonate', 'Naproxen', 'Minor', 'Antacids and some oral aluminum, calcium, or magnesium containing preparations may decrease the gastrointestinal absorption of enteric-coated naproxen, which requires an acidic environment for dissolution. The proposed mechanism is an increase in gastric pH (i.e. decreased gastric acidity) produced by antacids. In patients treated with antacids, the possibility of a reduced or subtherapeutic response to enteric-coated naproxen should be considered. Concomitant use of these drugs is generally not recommended. Aluminum hydroxide and some magnesium-containing antacids have also been reported to reduce the absorption of regular naproxen. The interaction may be minimized by separating the times of administration by at least 2 hours.', NULL, '-', NULL, '-', 'Absorption', 'Ritodrine, Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Phenylbutazone, Tolazoline, Ketoprofen, Piroxicam, Flurbiprofen, More', 'Sorbitol, Sulfamethizole, Sodium citrate, Magnesium citrate, Mannitol, Sodium chloride, Neomycin, Sodium acetate, Zinc sulfate, Magnesium sulfate, Arginine, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113332/', '[1] Segre EJ, Sevelius H, Varady J "Effects of antacids on naproxen absorption." N Engl J Med 291 (1974): 582-3[2] Brogden RN, Heel RC, Speight TM, Avery GS "Naproxen up to date: a review of its pharmacological properties and therapeutic efficacy and use in rheumatic diseases and pain states." Drugs 18 (1979): 241-77', 1767369485);
