INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113210, 'Valdecoxib', 'Naproxen', 'Moderate', 'Concomitant use of COX-2 inhibitors with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation. The risk is dependent on both dosage and duration of therapy.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided. Some authorities consider the use of COX-2 inhibitors with non-aspirin NSAIDs as contraindicated.', 'Synergism', 'Chondroitin sulfate, Naproxen, Glucosamine', 'Ritodrine, Bremelanotide, Flibanserin, Bromocriptine, Tolazoline, Dimethyl sulfoxide, Valdecoxib, Chondroitin sulfate, Esomeprazole, Glucosamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113341/', '[1] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113211, 'Vemurafenib', 'Naproxen', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2C8. The proposed mechanism, based on in vitro data, is decreased clearance due to vemurafenib-mediated inhibition of CYP450 2C8.', NULL, 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of CYP450 2C8, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever vemurafenib is added to or withdrawn from therapy with these drugs. Since vemurafenib has a long half-life, a wash-out period of 8 days following the discontinuation of vemurafenib has been suggested to avoid an interaction with subsequent therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Bremelanotide, Ibuprofen, Cabergoline, Rabeprazole, Indomethacin, Tolazoline, Ketoprofen, Diclofenac, Piroxicam, Flurbiprofen, Ibuprofen, More', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Darunavir, Tioconazole, Brinzolamide, Furazolidone, Diiodohydroxyquinoline, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113342/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Diabinese (chlorpropamide)." Pfizer U