INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (113213, 'Anisindione', 'Tetracycline', 'Moderate', 'Tetracyclines may increase the hypoprothrombinemic effects of warfarin and similar anticoagulants. The mechanism of interaction has not been established, but may involve depression of plasma prothrombin activity or suppression of vitamin K-producing bacteria in the gastrointestinal tract, although the latter may be of limited clinical importance except in patients whose dietary intake of vitamin K is severely reduced.', NULL, 'Caution is advised when warfarin and similar anticoagulants are used with tetracyclines.', NULL, 'Caution is advised when warfarin and similar anticoagulants are used with tetracyclines. The INR should be checked frequently and the anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of tetracycline therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Polymyxin B, Rifamycin, Chlorhexidine, Gentamicin, Misoprostol, Bismuth subcitrate potassium, Famotidine, Nizatidine, Ranitidine, Vonoprazan, Clotrimazole, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/113344/', '[1] "Product Information. Declomycin (demeclocycline)." Lederle Laboratories, Wayne, NJ.[2] Hasan SA "Interaction of doxycycline and warfarin: an enhanced anticoag