    ) VALUES (1391550, 'Clarithromycin', 'Nadolol', 'Minor', 'Some macrolide antibiotics may alter gastrointestinal flora. Biliary elimination of nadolol may be increased. The clinical implications of this interaction are not known. No special precautions are necessary.', NULL, '-', NULL, '-', 'Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
